class KnowledgeBase:
    def __init__(self):
        self.facts = {}
        self.nodes = {}  # Store node objects

    def add_node(self, node_id, node_type, properties):
        """Store a node object."""
        self.nodes[node_id] = {'type': node_type, 'properties': properties}

    def add_fact(self, entity1, relation, entity2):
        self.facts[(entity1, relation)] = entity2

    def infer(self, entity, *relations):
        current_entity = entity
        for relation in relations:
            key = (current_entity, relation)
            if key in self.facts:
                current_entity = self.facts[key]
            else:
                return None
        return current_entity

    def get_node(self, node_id):
        """Get the node object with all its properties."""
        return self.nodes.get(node_id)

